operation.sql:
INSERT INTO atom_config(
	id, catg, name, state, value, cvalue, gmt_create, gmt_modify
) VALUES(
	?, ?, ?, ?, ?, ?, ?, ?
)

operation.sqlmap:
INSERT INTO atom_config(
	id, catg, name, state, value, cvalue, gmt_create, gmt_modify
) VALUES(
	#{id}, #{catg}, #{name}, #{state}, #{value}, #{cvalue}, NOW(), NOW()
)
