operation.sqlmap:
INSERT INTO atom_image(
	id, flag, topic, title, size, width, height, url, summary, gmt_create, gmt_modify
) VALUES(
	#{id}, #{flag}, #{topic}, #{title}, #{size}, #{width}, #{height}, #{url}, #{summary}, NOW(), NOW()
)
